import React from "react";
import { MinusCircleOutlined } from '@ant-design/icons';
import { VideoInterface } from "../../interfaces/video";
import { Link } from "react-router-dom";
import { Button, Image } from "antd";
import axios from "axios";
;

const VideoPreview: React.FC<VideoInterface> = (props: VideoInterface) => {
  const revalidator = useRevalidator();

  const onDelete = async (id: number) => {
    const response = await axios.delete(`${process.env.REACT_APP_API_HOST}/videos/${id}`);

    if (response.status === 200) {
      console.log('Deleted!');
      revalidator.revalidate();
    }
  }

  return (
    <div style={{ margin: '15px', maxWidth: '300px' }}>
      <div style={{ marginBottom: '15px' }}>
        <Image
          width={277}
          preview={false}
          src={`https://loremflickr.com/277/275/music/?random=${props.id}`}
          alt="Video Preview" 
        />
      </div>
      <Link to={`/${props.id}`}>
        {props.name}
      </Link>
      <Button style={{ padding: 5, marginLeft: 5}} type="primary" color="red"><MinusCircleOutlined/> Delete</Button>
    </div>
  );
};

export default VideoPreview;
