import { Input, Button, Form } from "antd";

const onFinish = (values: any) => {
  console.log('Success:', values);
};

const onFinishFailed = (errorInfo: any) => {
  console.log('Failed:', errorInfo);
};

type FieldType = {
  name: string;
  url: string;
};

interface VideoFormProps {
  visible: boolean;
  onCreate: (values: FieldType) => void;
  setVisibility: (visible: boolean) => void;
}

const VideoForm: React.FC<VideoFormProps> = ({ visible, setVisibility, onCreate }) => {
  const [form] = Form.useForm();

  const handleCreate = () => {
    form.validateFields()
      .then((values) => {
        form.resetFields();
        onCreate(values);
      })
      .catch((info) => {
        console.log("Validate Failed:", info);
      });
  };


  return (
    <Form
      name="basic"
      labelCol={{ span: 8 }}
      wrapperCol={{ span: 16 }}
      style={{ maxWidth: 600 }}
      onFinish={onFinish}
      onFinishFailed={onFinishFailed}
      autoComplete="off"
    >
      <Form.Item<FieldType>
        label="Name"
        name="name"
        rules={[{ required: true, message: 'Please input the video name!' }]}
      >
        <Input />
      </Form.Item>

      <Form.Item<FieldType>
        label="URL"
        name="url"
        rules={[{ required: true }, { type: 'url' }, { type: 'string', min: 6 }]}
      >
        <Input placeholder="Video URL" />
      </Form.Item>

      <Form.Item wrapperCol={{ offset: 8, span: 16 }}>
        <Button type="primary" htmlType="submit">
          Submit
        </Button>
      </Form.Item>
    </Form>
  );
};

export default VideoForm;
