import React from 'react';
import { Layout, theme } from 'antd';
import { VideoInterface } from './interfaces/video';
import VideoPreview from './components/VideoPreview/VideoPreview';

import axios from 'axios';

const { Header, Content, Footer, Sider } = Layout;

const App: React.FC = () => {
  const { token: { colorBgContainer } } = theme.useToken();

  const [videos, setVideos] = React.useState([]);

  React.useEffect(() => {
    const fetchVideos = async () => {
      const response = await axios.get('http://localhost:8000/videos');
      console.log(response);

      const { data } = response;

      console.log(data)
      setVideos(data);
    };

    fetchVideos();
  }, []);

  return (
    <Layout style={{ height: '100svh' }}>
      <Layout>
        <Sider
          width='27%'
          breakpoint="lg"
          collapsedWidth="0"
          onBreakpoint={(broken) => {
            console.log(broken);
          }}
          onCollapse={(collapsed, type) => {
            console.log(collapsed, type);
          }}
        >
        </Sider>
        <Layout>
          <Header style={{ padding: 0, background: colorBgContainer }} />
          <Content style={{}}>
            { videos.map((video: VideoInterface) => <VideoPreview key={video.id} { ...video } />) }
          </Content>
        </Layout>
      </Layout>
      <Footer style={{ height: '10svh', textAlign: 'center', backgroundColor: 'gray' }}>
        Ant Design Â©2023 Created by Ant UED
      </Footer>
    </Layout>
  );
};

export default App;
