import { put, takeEvery } from 'redux-saga/effects';
import { actionTypes, getVideos } from '../actions/videoActions';
import { getAllVideos, getVideo } from "../requests/video.requests";

// Get All Videos
async function* fetchVideos() {
  try {
    const videos = await getAllVideos();
    yield videos;

    yield put(getVideos(videos));
  } catch (error) {

  }
}

// Get Video
async function* fetchVideo(id: number) {
  try {
    const video = await getVideo(id);
    yield video;

    yield put(getVideos(video));
  } catch (error) {

  }
}

export function* watchFetchVideos() {
  yield takeEvery(actionTypes.GET_VIDEOS, fetchVideos);
}

export function* watchFetchVideo() {
  yield takeEvery(actionTypes.GET_VIDEO, fetchVideo);
}
