import axios from "axios";
import { LoaderFunctionArgs, useLoaderData } from "react-router-dom";
import { VideoInterface } from "../interfaces/video";

export const videoLoader = async ({ params }: LoaderFunctionArgs) => {
  const response = await axios.get(`http://localhost:8000/videos/${params.id}`);
  const { data } = response;
  return { video: data };
};

const VideoPlayback: React.FC = () => {
  const { video } = useLoaderData() as { video: VideoInterface };

  const isYoutube = video.url.includes('youtube.com');

  if (isYoutube) {
    return (
      <>
      <div style={{ height: '80%', width: '100%'}}>
      <h1>{video.name}</h1>

        <iframe
        style={{ height: '100%', width: '100%', position: 'relative' }}
          src={video.url.replace('watch?v=', 'embed/')}
          title="YouTube video player"
          allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
        >
        </iframe>
      </div>
      </>
    );
  }

  return (
    <div>
      <h1>{video.name}</h1>
      <video controls style={{ width: '100%' }}>
        <source src={video.url} type="video/mp4" />
      </video>
    </div>
  );
}

export default VideoPlayback;
