import React from 'react';
import { Layout, theme } from 'antd';

import axios from 'axios';
import VideoGrid from '../components/VideoGrid/VideoGrid';

const { Header, Content, Footer, Sider } = Layout;

const Main = () => {
  const { token: { colorBgContainer } } = theme.useToken();

  const [videos, setVideos] = React.useState([]);

  React.useEffect(() => {
    const fetchVideos = async () => {
      const response = await axios.get('http://localhost:8000/videos');
      const { data } = response;
      setVideos(data);
    };

    fetchVideos();
  }, []);

  return (
    <Layout style={{ height: '100svh' }}>
      <Layout>
        <Sider
          width='15%'
          breakpoint="lg"
          collapsedWidth="0"
          onBreakpoint={(broken) => {
            console.log(broken);
          }}
          onCollapse={(collapsed, type) => {
            console.log(collapsed, type);
          }}
        >
        </Sider>
        <Layout>
          <Header style={{ height: '10%', padding: 0, background: colorBgContainer }} />
          <Content style={{}}>
            <VideoGrid videos={videos} />
            <></>
          </Content>
        </Layout>
      </Layout>
      <Footer style={{ height: '10svh', textAlign: 'center', backgroundColor: 'gray' }}>
        Ant Design Â©2023 Created by Ant UED
      </Footer>
    </Layout>
  );
};

export default Main;
