import React from "react";
import { VideoInterface } from "../../interfaces/video";
import { Link } from "react-router-dom";
import { Image, Layout } from "antd";

const { Header, Content, Footer, Sider } = Layout;


const VideoPreview: React.FC<VideoInterface> = (props: VideoInterface) => {
  const { token: { colorBgContainer } } = theme.useToken();

  return (
    <Layout style={{ height: '100svh' }}>
      <Layout>
        <Sider
          width='15%'
          breakpoint="lg"
          collapsedWidth="0"
          onBreakpoint={(broken) => {
            console.log(broken);
          }}
          onCollapse={(collapsed, type) => {
            console.log(collapsed, type);
          }}
        >
        </Sider>
        <Layout>
          <Header style={{ height: '10%', padding: 0, background: colorBgContainer }} />
          <Content style={{}}>
            <div style={{ margin: '15px', maxWidth: '300px' }}>
              <Image
                width={277}
                preview={false}
                src={`https://loremflickr.com/277/275/music/?random=${props.id}`}
                alt="Video Preview" 
              />
              <Link to={`/${props.id}`}>
                <h4>{props.name}</h4>
              </Link>
            </div>
          </Content>
        </Layout>
      </Layout>
      <Footer style={{ height: '10svh', textAlign: 'center', backgroundColor: 'gray' }}>
        Ant Design Â©2023 Created by Ant UED
      </Footer>
    </Layout>
  );
};

export default VideoPreview;
