import { createAction } from '@reduxjs/toolkit';
import { VideoInterface } from '../interfaces/video';

// Action types
export const actionTypes = {
  CREATE_VIDEO: 'CREATE_VIDEO',
  GET_VIDEOS: 'GET_VIDEOS',
  UPDATE_VIDEO: 'UPDATE_VIDEO',
  DELETE_VIDEO: 'DELETE_VIDEO',
};

// Action creators
export const createVideo = createAction<VideoInterface>(actionTypes.CREATE_VIDEO);
export const getVideos = createAction<VideoInterface[]>(actionTypes.GET_VIDEOS);
export const updateVideo = createAction<VideoInterface>(actionTypes.UPDATE_VIDEO);
export const deleteVideo = createAction(actionTypes.DELETE_VIDEO);
