import React from "react";
import { VideoInterface } from "../../interfaces/video";
import { Link, LoaderFunctionArgs } from "react-router-dom";
import { Image } from "antd";
import axios from "axios";

export const videoLoader = async ({ params }: LoaderFunctionArgs) => {
  const response = await axios.get(`http://localhost:8000/videos/${params.id}`);
  const { data } = response;
  return { videos: data };
};

const VideoPreview: React.FC<VideoInterface> = (props: VideoInterface) => {
  return (
    <div style={{ margin: '15px', maxWidth: '300px' }}>
      <Image
        width={277}
        preview={false}
        src={`https://loremflickr.com/277/275/music/?random=${props.id}`}
        alt="Video Preview" 
      />
      <Link to={`/${props.id}`}>
        <h4>{props.name}</h4>
      </Link>
      {/* <div style={{ margin: '15px', maxWidth: '300px' }}>
        <img src={`https://loremflickr.com/277/275/music/?random=${props.id}`} alt="Video Preview" />
        <h3>{props.name}</h3>
      </div> */}
    </div>
  );
};

export default VideoPreview;
